import React, { useEffect, useState } from "react";

import "./styles/style.css";

function TodoList() {
  const [todos, setTodos] = useState([]);
  const [newTodoText, setNewTodoText] = useState("");
  const [isChecked, setIsChecked] = useState(false);

  // useEffect(() => {
  //   const data = localStorage.getItem("todos");
  //   setTodos(JSON.parse(data));
  // }, []);

  useEffect(() => {
    localStorage.setItem("todos", JSON.stringify(todos));
  }, [todos]);

  const addTodo = () => {
    setTodos((todos) => [...todos, { id: Date.now(), text: newTodoText, checked: isChecked }]);
    setNewTodoText("");
  };

  const checkedHandler = (e) => {

    setIsChecked(!isChecked);

  }

  const handleNewTodoTextChange = (event) => {
    setNewTodoText(event.target.value);
  };

  const handleDeleteTodo = (id) => {
    setTodos((todos) => todos.filter((todo) => todo.id !== id));
  };

  return (
    <div className="App">
      <h1>Todo List</h1>
      <div className="add-todo">
        <input
          type="text"
          value={newTodoText}
          onChange={handleNewTodoTextChange}
          placeholder="Enter new todo"
        />
        <button onClick={addTodo}>Add Todo</button>
      </div>
      <ul>
        {todos.map((todo) => (
          <li key={todo.id}>
            <label><input id={todo.id} onChange={checkedHandler} type='checkbox' txt={todo.text}/>{todo.text}</label><button onClick={() => handleDeleteTodo(todo.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default TodoList;
