import React, { useRef } from 'react';

const img1 = './1_b.jpeg'
const img2 = './2_b.jpeg'
const img3 = './1.jpg'
const img4 = './2.jpg'

const ImageToggleOnMouseOver = ({primaryImg, secondaryImg}) => {
  const imageRef = useRef(null);

  return (
    <img 
      onMouseOver={() => {
        imageRef.current.src = secondaryImg;
      }}
      onMouseOut={() => {
        imageRef.current.src= primaryImg;
      }}
      src={primaryImg} 
      alt=""
      ref={imageRef}
    />
  )
}

const ImageChangeOnMouseOver = () => {
  return (
    <div>
      <ImageToggleOnMouseOver
        primaryImg={img1}
        secondaryImg={img2}
        alt="" />
      &nbsp; &nbsp; &nbsp;
      <ImageToggleOnMouseOver
        primaryImg={img3}
        secondaryImg={img4}
        alt="" />
    </div>
  )
}

const App = () => {

  return (
    <div>
      <ImageChangeOnMouseOver/>
    </div>
  );
};

export default App;
