import React, { useRef } from "react";
import "./App.css";

const App = () => {
  const img1Ref = useRef(null);
  const img2Ref = useRef(null);

  // function handleMouseEnter(e) {
  //   e.target.getAttribute('class') === "img1"
  //     ? img1Ref.current.background = `url("./1.jpg") no-repeat center center/cover`
  //     : img2Ref.current.background = `url("./2.jpg") no-repeat center center/cover`;
  // }

  //   function handleMouseLeave(e) {
  //   e.target.getAttribute('class') === "img1"
  //     ? img1Ref.current.background = `url("./1_b.jpeg") no-repeat center center/cover`
  //     : img2Ref.current.background = `url("./2_b.jpeg") no-repeat center center/cover`;
  // }

  return (
    <>
      <img
        className="img1"
        ref={img1Ref}
        // onMouseEnter={handleMouseEnter}
        // onMouseLeave={handleMouseLeave}
        onMouseOver={() => img1Ref.current.background = `url("./1.jpg") no-repeat center center/cover`}
        onMouseOut={() => img1Ref.current.background = `url("./1_b.jpeg") no-repeat center center/cover`}
      ></img>
      <img
        className="img2"
        ref={img2Ref}
        onMouseOver={() => img2Ref.current.backgroundImage = `url("./2.jpg") no-repeat center center/cover`}
        onMouseOut={() => img2Ref.current.backgroundImage = `url("./2_b.jpeg") no-repeat center center/cover`}
      ></img>
    </>
  );
};

export default App;
