import './App.css';
import CustomButton from './CustomButton';
import { useState } from 'react';

function App() {
  [color, setColor] = useState('');
  const colors = ["blue","red","yellow"];

  function setColor(color) {
    colorVar = color;
  }

  return (
    <>
      {colors.map((color) => <CustomButton onClick={setColor(color)} color={color}/>)}

    <h1>THE COLOR IS : {colorVar}</h1>
    </>
  );
}

export default App;
