import React from 'react';
import ToDoList from './ToDoList';

const Button = ({input}) => {

  const messages = JSON.parse(localStorage.getItem('messages'));
  messages.push({
    id: [messages.length],
    msg: [input],
    isDone: false
  });

  localStorage.setItem('messages', messages);

  return (
    <>
      <button>Add</button>
      <ToDoList msg={input}/>
    </>
  )
}

export default Button
