import React from 'react'
import './Card.css';

const Card = (props) => {
  const validate = (top) => {
    if (!props.toppingsArr.find(top.name)) {
      props.toppingsHandler(oldArray => [...oldArray, top.name]);
    }else{
      props.errorMsgHandler('cannot duplicate')
    }
  }
  return (
    <div className='card'>
      <h3>{props.turtle.name}</h3>
      <img src={props.turtle.img} alt={props.turtle.name}/>
      <h4>Toppings</h4>
      {props.turtle.pizzaToppings.map((top) => <button key={top.img} onClick={validate(top)}>{top.name}</button>)}
    </div>
  )
}

export default Card
