import React, { useState } from 'react';
import './App.css';
import data from './data.js';
import Card from './Card'

const App = () => {
  const [errorMsg, setErrorMsg] = useState('');
  const [toppings, setToppings] = useState([]);
  const dataArr = [...data];

  const toppingsHandler = (turtle) => {

    if (!toppings.find((name) => turtle.name === name)) {
      setToppings(oldArray => [...oldArray, turtle.name]);
    }else{
      errorHandler('cannot duplicate');
    }
  }

  const errorHandler = (text) => {
    setErrorMsg(text);
  }

  return (
    <div className='./App.css'>
      <h1>Ninja Turtles</h1>
      <h2>Order Pizza with the turtles</h2>
      <div className='flex'>
        {dataArr.map((element) => <Card key={element.img} toppingsHandler={toppingsHandler} turtle={element}/>)}
      </div>
      <h4>Our order:</h4>
      <div classList='toppings'>{toppings}</div>
      <div classList='errorMessage'>{errorMsg}</div>
    </div>

  )
}

export default App;
