import React, { useState } from 'react';
import './App.css';
import data from './data.js';
import Card from './Card'

const App = () => {
  const [errorMsg, setErrorMsg] = useState('');
  const [toppings, setToppings] = useState([]);
  const dataArr = [...data];
  return (
    <div className='./App.css'>
      <h1>Ninja Turtles</h1>
      <h2>Order Pizza with the turtles</h2>
      <div className='flex'>
        {dataArr.map((element) => <Card key={element.img} errorMsgHandler={setErrorMsg} toppingsHandler={setToppings} turtle={element}/>)}
      </div>
      <h4>Our order:</h4>
      <div classList='toppings'>{toppings}</div>
      <div classList='errorMessage'>{errorMsg}</div>
    </div>

  )
}

export default App;
