import React from 'react';
import ToDoList from './ToDoList';

const Button = ({input}) => {

  //const messages = JSON.parse(localStorage.getItem('messages'));
  const msg = input;
  const messages = [];
  messages.push({
    id: messages.length,
    msg: input,
    isDone: false
  });

  localStorage.setItem('messages', JSON.stringify(messages));

  return (
    <>
      <button>Add</button>
      <ToDoList msg={msg}/>
    </>
  )
}

export default Button
