import React, { useRef } from "react";
import "./App.css";

const App = () => {
  const img1Ref = useRef(null);
  const img2Ref = useRef(null);

  function handleMouseEnter(img) {
    img === "img1"
      ? img1Ref.current.background = `url("./1.jpg") no-repeat center center/cover`
      : img2Ref.current.background = `url("./2.jpg") no-repeat center center/cover`;
  }

    function handleMouseLeave(img) {
    img === "img1"
      ? (img1Ref.current.src = "./1_b.jpeg")
      : (img2Ref.current.src = "./2_b.jpeg");
  }

  return (
    <>
      <div
        className="img1"
        ref={img1Ref}
        onMouseEnter={handleMouseEnter("img1")}
        onMouseLeave={handleMouseLeave("img1")}
      ></div>
      <div
        className="img2"
        ref={img2Ref}
        onMouseEnter={handleMouseEnter("img2")}
        onMouseLeave={handleMouseLeave("img2")}
      ></div>
    </>
  );
};

export default App;
