import React, { useRef } from 'react';
import './App.css';



const App = () => {

  const img1Ref = useRef(null);
  const img2Ref = useRef(null);

  function handleMouseEnter(img, imagePath) {
      img === 'img1' ? img1Ref.current.src = './1_b.jpeg' : img2Ref.current.src = './2_b.jpeg';
    }

  return (
    <>
      <div src='./1.jpg' alt='img1' width='640px' height='480px' ref={img1Ref} onMouseEnter={handleMouseEnter('img1', './1_b.jpeg')}></div>
      <div src='./2.jpg' alt='img2' width='640px' height='480px' ref={img2Ref} onMouseEnter={handleMouseEnter('img2', './2_b.jpeg')}></div>
    </>

  )
}

export default App;
